type endpointsTemplate struct {
	Info *GenerationInfo
	grpc bool
}

func NewEndpointsTemplate(info *GenerationInfo) Template {
	return &endpointsTemplate{
		Info: info,
	}
}

func endpointStructName(str string) string {
	return str + "Endpoint"
}

